import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [isScrolled, setIsScrolled] = useState(false);

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    {
      label: 'Register',
      dropdown: [
        { label: 'Sub Item 1' },
        { label: 'Sub Item 2' },
        { label: 'Sub Item 3' },
      ],
    },
    {
      label: 'Login',
      dropdown: [
        { label: 'Sub Item 1' },
        { label: 'Sub Item 2' },
        { label: 'Sub Item 3' },
      ],
    },
    { label: 'HOME' },
    {
      label: 'BUY',
      dropdown: [
        { label: 'Sub Item 1' },
        { label: 'Sub Item 2' },
        { label: 'Sub Item 3' },
      ],
    },
    {
      label: 'SELL',
      dropdown: [
        { label: 'Sub Item 1' },
        { label: 'Sub Item 2' },
      ],
    },
    { label: 'RESELL' },
    { label: 'ABOUT' },
    { label: 'CONTACT' },
  ];

  return (
    <div className="container">
      <header className={isScrolled ? 'scrolled' : ''}>
        <div className="header-content">
          <div className="logo">
            <img src="/path/to/your/logo.png" alt="Logo" />
          </div>
          <nav className="header-links">
            {navItems.map((item, index) => (
              <div key={index} className="nav-item">
                <a href={`#${item.label.toLowerCase().replace(' ', '-')}`} onClick={() => toggleDropdown(index)}>
                  {item.label}
                </a>
                {item.dropdown && activeDropdown === index && (
                  <div className="dropdown-content">
                    {item.dropdown.map((subItem, subIndex) => (
                      <a key={subIndex} href={`#${subItem.label.toLowerCase().replace(' ', '-')}`}>
                        {subItem.label}
                      </a>
                    ))}
                  </div>
                )}
              </div>
            ))}
            <button className="book-collection">BOOK A COLLECTION</button>
          </nav>
        </div>
      </header>


      <footer>
        <div className="footer-content">
          <div className="footer-links">
            <a href="#terms">Terms & Conditions</a>
            <a href="#privacy">Privacy Policy</a>
            <a href="#contact">Contact Us</a>
          </div>
          <div className="social-links">
            <a href="#facebook"><i className="fab fa-facebook-f"></i></a>
            <a href="#twitter"><i className="fab fa-twitter"></i></a>
            <a href="#instagram"><i className="fab fa-instagram"></i></a>
          </div>
        </div>
        <div className="copyright">
          <p>&copy;  Scrap Spot. We Deal with RuestGold.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
