import React, { useState, useEffect } from 'react';

import './App.css';

function App() {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [isScrolled, setIsScrolled] = useState(false);
  const [doorOpen, setDoorOpen] = useState(false);

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setDoorOpen(!doorOpen);
    }, 1000);
    return () => clearInterval(intervalId);
  }, []);

  const navItems = ['Register', 'Login', 'HOME', 'BUY', 'SELL', 'RESELL', 'ABOUT', 'CONTACT'];

  return (
    <div className="container">
      <header className={isScrolled ? 'scrolled' : ''}>
        <div className="header-content">
          <div className="logo">
            <img src="src/logo.jpeg" alt="Logo" />
            {/* Your scrap, our mission */}
          </div>
          <nav className="header-links">
            {navItems.map((item, index) => (
              <div key={index} className="nav-item">
                <a
                  href={`#${item.toLowerCase().replace(' ', '-')}`}
                  onClick={() => toggleDropdown(index)}
                >
                  {item}
                </a>
                {shouldRenderDropdown(item, index) && (
                  <div className="dropdown-content">
                    {/* Customize dropdown content based on item */}
                    {renderDropdownContent(item)}
                  </div>
                )}
              </div>
            ))}
            <button className="book-collection">BOOK A COLLECTION</button>
          </nav>
        </div>
      </header>

      <main>
        <section className="hero">
          <div className="hero-content">
            <h1>
              <b>
                Scrap smart, live green Schedule your pickup today 
                <span className={`door-step ${doorOpen ? 'open' : ''}`}>
                  <div className="door">
                    <div className="step"></div>
                  </div>
                  make yourself ScrapFree
                </span>
              </b>
            </h1>
            <p>
              Is your storeroom filled with scrap and old things? You are at the right place. It's
              time to clean and sell your scrap to us. Sell your Kabad to us.
            </p>

            <button className="book-collection">BOOK A COLLECTION</button>
          </div>
        </section>

        <section className="about">
          <h2>About Scrap Spot</h2>
          <p>
            Scrap Spot is a start-up based in Pune. We are a fully technology-driven module to
            organize the unsegmented work into a fully automated, professional, and online waste
            management brand.
          </p>
          <p>
            Our mission is to provide a seamless and efficient waste management experience to
            households, businesses, and institutions, while promoting sustainability and reducing
            the environmental impact of waste disposal. By leveraging cutting-edge technology and
            innovative logistics, we strive to make waste management more accessible, convenient,
            and eco-friendly.
          </p>
          <p>
            At Scrap Spot, we believe that waste management is not just about collecting trash, but
            about creating a circular economy that benefits both people and the planet. Our
            platform is designed to connect waste generators with authorized recyclers and waste
            processors, ensuring that waste is handled in an environmentally responsible manner.
          </p>
        </section>

        <section className="services">
          <h2>Our Services</h2>
          <div className="service-cards">
            <div className="service-card">
              <i className="fas fa-recycle"></i>
              <h3>Recycling</h3>
              <p>We recycle various materials to help preserve our environment.</p>
            </div>
            <div className="service-card">
              <i className="fas fa-truck"></i>
              <h3>Door-step Collection</h3>
              <p>We collect scrap materials directly from your doorstep.</p>
            </div>
            <div className="service-card">
              <i className="fas fa-money-bill-wave"></i>
              <h3>Best Rates</h3>
              <p>We offer competitive rates for your scrap materials.</p>
            </div>
          </div>
        </section>

        {/* Render RegistrationPage component
        <RegistrationPage /> */}

      </main>


<footer>
  <div className="footer-content">
    <div className="footer-links">
      <a href="#terms">Terms & Conditions</a>
      <a href="#privacy">Privacy Policy</a>
      <a href="#contact">Contact Us</a>
    </div>
    <div className="social-links">
      <a href="#facebook">
        <i className="fab fa-facebook-f"></i>
      </a>
      <a href="#twitter">
        <i className="fab fa-twitter"></i>
      </a>
      <a href="#instagram">
        <i className="fab fa-instagram"></i>
      </a>
    </div>
  </div>
  <div className="copyright">
    <p>&copy; Scrap Spot. We Deal with RuestGold.</p>
  </div>
</footer>
</div>
);
}

function shouldRenderDropdown(item, index) {
return ['BUY', 'SELL', 'Login'].includes(item);
}

function renderDropdownContent(item) {
switch (item) {
case 'BUY':
return (
  <div className="dropdown-content">
    <a href="#buy-sub-1">Sub Item 1</a>
    <a href="#buy-sub-2">Sub Item 2</a>
    <a href="#buy-sub-3">Sub Item 3</a>
  </div>
);
case 'SELL':
return (
  <div className="dropdown-content">
    <a href="#sell-sub-1">Sub Item 1</a>
    <a href="#sell-sub-2">Sub Item 2</a>
    <a href="#sell-sub-3">Sub Item 3</a>
  </div>
);
case 'Login':
return (
  <div className="dropdown-content">
    <a href="#login-admin">Admin</a>
    <a href="#login-company">Company</a>
    <a href="#login-yard">Yard</a>
    <a href="#login-customer">Customer</a>
  </div>
);
default:
return null;
}
}

export default App;
