import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeDropdown, setActiveDropdown] = useState(null);

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  return (
    <div className="container">
      <header>
        <div className="header-content">
          <div className="logo">
            <img src="c:\Users\HP\Desktop\final project\scrapdeal\logo.jpeg" alt="Logo" />
          </div>
          <nav className="header-links">
            <div className="dropdown">
              <a href="#home" onClick={() => toggleDropdown(0)}>HOME</a>
              {activeDropdown === 0 && (
                <div className="dropdown-content">
                  <a href="#welcome">Welcome</a>
                  <a href="#services">Our Services</a>
                  <a href="#testimonials">Testimonials</a>
                </div>
              )}
            </div>
            <div className="dropdown">
              <a href="#services" onClick={() => toggleDropdown(1)}>SCRAP SPOT</a>
              {activeDropdown === 1 && (
                <div className="dropdown-content">
                  <a href="#paper">Paper</a>
                  <a href="#metal">Metal</a>
                  <a href="#electronics">Electronics</a>
                </div>
              )}
            </div>
            <div className="dropdown">
              <a href="#about" onClick={() => toggleDropdown(2)}>ABOUT</a>
              {activeDropdown === 2 && (
                <div className="dropdown-content">
                  <a href="#our-story">Our Story</a>
                  <a href="#mission">Mission</a>
                  <a href="#team">Our Team</a>
                </div>
              )}
            </div>
            <a href="#rate-list">RATE LIST</a>
            <div className="dropdown">
              <a href="#contact" onClick={() => toggleDropdown(3)}>CONTACT</a>
              {activeDropdown === 3 && (
                <div className="dropdown-content">
                  <a href="#phone">Phone</a>
                  <a href="#email">Email</a>
                  <a href="#address">Address</a>
                </div>
              )}
            </div>
            <button className="book-collection">BOOK A COLLECTION</button>
          </nav>
        </div>
      </header>

      {/* Rest of the component remains the same */}

    </div>
  );
}

export default App;
